import java.util.Scanner;import java.awt.Toolkit;import java.awt.datatransfer.StringSelection;import java.util.Base64;
class lyrics{static String c(String w){return w.length()<=1?w:w.charAt(0)+"*".repeat(Math.max(0,w.length()-2))+w.charAt(w.length()-1);}static String f(String t){String[]r=new String(Base64.getDecoder().decode("ZnVjayxzaGl0LGRhbW4sY3JhcCxiaXRjaCxhc3Nob2xlLGRpY2ssY29jaxxwdXNzeSx3YW5rLGJhc3RhcmQsYm9sbG9ja3MsY3VudA==")).split(",");String[]w=t.split("\\s+");for(int i=0;i<w.length;i++){String l=w[i].toLowerCase().replaceAll("[^a-zA-Z]","");for(String s:r)if(l.equals(s)){w[i]=c(w[i]);break;}}return String.join(" ",w);}public static void main(String[]a){Scanner s=new Scanner(System.in);String t="",r="",x="",y="";boolean l=false;System.out.println("TRACK_NAME / x\nARTIST_NAME / LYRICS");t=s.nextLine();if(t.equals("x"))l=true;else for(int i=0;i<t.length();i++){String q=t.substring(i,i+1);x+=(q.equals(" "))?"+":q;}r=s.nextLine();if(l){y=r.replace("\\n","\n").replace("\"","");y=f(y);StringSelection v=new StringSelection(y);Toolkit.getDefaultToolkit().getSystemClipboard().setContents(v,null);System.out.println("\n"+y);}else{for(int i=0;i<r.length();i++){String q=r.substring(i,i+1);y+=(q.equals(" "))?"+":q;}System.out.println("https://lrclib.net/api/get?artist_name="+y+"&track_name="+x);}s.close();}}